import React, { Component } from 'react'
import { NavLink } from 'react-router-dom'

export default class Header extends Component {
    state = {
        flag: false,
        width:window.innerWidth
    }
    componentDidUpdate(){
        console.log(this.state.width);

    }
    render() {
        const handleMenuClick = () => {
            this.setState({flag:!this.state.flag})
        }
        console.log(this.state.width);
        return (
            <div className='flex header'>
                <img src="./assets/Logo.png" alt='kuants_logo' className="main-logo" />
                <div className='menu-container'>
                    <img src='./assets/square.png' alt='hamburger-icon' className='menu-icon' onClick={handleMenuClick} />
                    <nav >
                        <NavLink
                            to="/home"
                            className={({ isActive }) => (isActive ? 'active' : 'inactive')}
                        onClick={()=>this.setState({flag:false})}>
                            Home
                        </NavLink>
                        <NavLink
                            to="/"
                            className={({ isActive }) => (isActive ? 'active' : 'inactive')}
                            onClick={()=>this.setState({flag:false})}
                        >
                            About
                        </NavLink>
                        <NavLink
                            to="/"
                            className={({ isActive }) => (isActive ? 'active' : 'inactive')}
                            onClick={()=>this.setState({flag:false})}
                        >
                            Team
                        </NavLink>
                        <NavLink
                            to="/"
                            className={({ isActive }) => (isActive ? 'active' : 'inactive')}
                            onClick={()=>this.setState({flag:false})}
                        >
                            Contact
                        </NavLink>
                        <div className='nav-dl-btn-container'>
                            <button className="nav-dl-btn" href="#">Download App</button>
                        </div>
                    </nav>
                    <nav style={{display:`${this.state.flag?'flex':'none'}`}}>
                        <NavLink
                            to="/home"
                            className={({ isActive }) => (isActive ? 'active' : 'inactive')}
                        onClick={()=>this.setState({flag:false})}>
                            Home
                        </NavLink>
                        <NavLink
                            to="/"
                            className={({ isActive }) => (isActive ? 'active' : 'inactive')}
                            onClick={()=>this.setState({flag:false})}
                        >
                            About
                        </NavLink>
                        <NavLink
                            to="/"
                            className={({ isActive }) => (isActive ? 'active' : 'inactive')}
                            onClick={()=>this.setState({flag:false})}
                        >
                            Team
                        </NavLink>
                        <NavLink
                            to="/"
                            className={({ isActive }) => (isActive ? 'active' : 'inactive')}
                            onClick={()=>this.setState({flag:false})}
                        >
                            Contact
                        </NavLink>
                        <div className='nav-dl-btn-container'>
                            <button className="nav-dl-btn" href="#">Download App</button>
                        </div>
                    </nav>

                </div>

            </div>
        )
    }
}
